// Loading
(function($){
$(window).on("load",function(){
$(function () {
$.each($('.preloader'), function() {
new Preloader($(this));
});
});
var Preloader = function(obj) {
var _window = $(window),
_obg = obj;
var _onEvents = function() {	
setTimeout(function(){ 
$('.preloader').addClass('loaded');
},1000)
},
_init = function() {
_onEvents();
};
_init()
};
});
})(jQuery);


